import styled from "styled-components";

const StyledChartContainer = styled.section`
  border: solid 1px grey;
  border-radius: 8px;
  height: 320px;
  margin: 2% 11%;
  width: 600px;
  h3 {
    display: flex;
    justify-content: center;
    margin: 2%;
  }
`;

const StyledMainContainer = styled.div`
  display: grid;
  grid-template-columns: 40% 40%;
  justify-content: center;
  margin-top: 10px;
`;

const StyledValue = styled.span`
  display: flex;
  font-size: 10rem;
  justify-content: center;
  width: 100%;
`;

const StyledTitle = styled.h2`
  border-bottom: 1px solid #4e4b4b4d;
  color: var(--primary-titles);
  font-size: 1.5rem;
  margin: 10px;
  padding: 10px;
`;

export { StyledMainContainer, StyledChartContainer, StyledValue, StyledTitle };
