};

Table.propTypes = {
  columns: PropTypes.array,
  data: PropTypes.array,
  className: PropTypes.string,
  callBack: PropTypes.func,
  callBackEdit: PropTypes.func,
  callBackDelete: PropTypes.func,
  path: PropTypes.string,
};

export default Table;
