import { withFormik } from "formik";
import * as yup from "yup";
import { REGEX } from "../../../../constants";
import EditUserForm from "./component/Form";
import { useIntl } from "react-intl";
const validationSchema = (intl) => {
  yup.object().shape({
    email: yup
      .string()
      .trim()
      .matches(
        REGEX.EMAIL_REGEX_SCHEMA,
        intl.formatMessage({ id: "userEdition.emailError" })
      )
      .max(30)
      .required(" "),
    name: yup
      .string()
      .trim()
      .matches(REGEX.NAME_REGEX, "userEdition.nameError")
      .required("userEdition.nameRequired"),
    surname: yup
      .string()
      .trim()
      .matches(REGEX.SURNAME_REGEX, "userEdition.surnameError")
      .required("userEdition.surnameRequired"),
    address: yup.string().trim().required("userEdition.addressRequired"),
    dni: yup.number(),
    // .trim()
    // .matches(REGEX.DNI_REGEX, "userEdition.dniError")
    // .required("userEdition.dniRequired"),
  });
};
//formik validations
const EditUser = withFormik({
  validateOnChange: true,
  enableReinitialize: false,
  validationSchema: (data) => {
    console.log(data)
    yup.object().shape({
      email: yup
        .string()
        .trim()
        .matches(
          REGEX.EMAIL_REGEX_SCHEMA,
          data.intl.formatMessage({ id: "userEdition.emailError" })
        )
        .max(30)
        .required(" "),
      name: yup
        .string()
        .trim()
        .matches(REGEX.NAME_REGEX, "userEdition.nameError")
        .required("userEdition.nameRequired"),
      surname: yup
        .string()
        .trim()
        .matches(REGEX.SURNAME_REGEX, "userEdition.surnameError")
        .required("userEdition.surnameRequired"),
      address: yup.string().trim().required("userEdition.addressRequired"),
      dni: yup.number(),
      // .trim()
      // .matches(REGEX.DNI_REGEX, "userEdition.dniError")
      // .required("userEdition.dniRequired"),
    });
  },
  mapPropsToValues: ({ data }) => ({
    email: data.email || "",
    name: data.name || "",
    surname: data.surname || "",
    address: data.address || "",
    dni: data.dni || "",
  }),
  handleSubmit: (value, { props, setErrors }) => {
    const {
      data: { dni },
    } = props;
    props.handleAsyncConfirm(dni, value).then((res) => {
      props.posResponse();
      props.onClose({ type: "hide" });
    });
  },
})(EditUserForm);

export default EditUser;
