import React, { useEffect, useState } from "react";
import { useIntl } from "react-intl";
import { io } from "socket.io-client";
import { FormattedMessage } from "react-intl";
import { moment } from "moment";
import {
  StyledChartContainer,
  StyledMainContainer,
  StyledValue,
} from "./styled-components";
import BarChart from "./components/BarChart";
import SystemLineChart from "./components/SystemLineChart";

var connectionOptions = {
  "force new connection": true,
  reconnectionAttempts: "Infinity", //avoid having user reconnect manually in order to prevent dead clients after a server restart
  timeout: 10000, //before connect_error and connect_timeout are emitted.
  transports: ["websocket"],
};

const System = () => {
  const intl = useIntl();
  const [memory, setMemory] = useState([]);
  const [cpu, setCpu] = useState("");
  const [network, setNetwork] = useState("");
  const [seconds, setSeconds] = useState([]);

  useEffect(() => {
    const socket = io("http://localhost:4000", connectionOptions);
    socket.on("connection");
    socket.on("cpu", (data) => {
      setCpu(data);
    });
    socket.on("memory", (data) => {
      // let incomingMemory = [data];
      // let array = [memory, ...incomingMemory]
      // setMemory(array);
    });
    socket.on("seconds", (data) => {
      setSeconds([data, data + 1, data + 2]);

      // if (seconds.length >= 10) {
      //   setSeconds([]);
      // }
    });
    socket.on("network", (data) => {
      setNetwork(data);
    });
    return () => {
      socket.disconnect();
    };
  }, []);
  console.log("seconds", seconds);
  // console.log("memory", memory);
  const DataUsage = {
    // labelsDataUsage: seconds,
    barColor: "rgba(255, 99, 132, 0.2)",
    title: "barChartDatatUsage.title",
    borderColor: "rgba(255, 99, 132, 1)",
  };
  const DataCpuUsage = {
    labelsDataUsage: [1, 2, 3, 4, 5, 6, 7],
    // [
    //   intl.formatMessage({ id: "barChartSystemUsage.hourAgo" }),
    //   intl.formatMessage({ id: "barChartSystemUsage.now" }),
    // ],
    barColor: "rgba(255, 206, 86, 0.2)",
    title: "barChartCpuUsage.title",
    borderColor: "rgba(255, 206, 86, 1)",
  };
  const DataMemoryUsage = {
    // labelsDataUsage: seconds,
    // [
    //   intl.formatMessage({ id: "barChartSystemUsage.hourAgo" }),
    //   intl.formatMessage({ id: "barChartSystemUsage.now" }),
    //   intl.formatMessage({ id: "barChartSystemUsage.hourAgo" }),
    //   intl.formatMessage({ id: "barChartSystemUsage.now" }),
    // ],
    barColor: "rgba(54, 162, 235, 0.2)",
    title: "barChartMemoryUsage.title",
    borderColor: "rgba(54, 162, 235, 1)",
  };
  return (
    <StyledMainContainer>
      <StyledChartContainer>
        <h3>
          {" "}
          <FormattedMessage id={"barChartCpuUsage.title"} />
        </h3>
        <StyledValue>
          {cpu} <label>%</label>
        </StyledValue>
        {/* <BarChart
          labels={DataUsage.labelsDataUsage}
          title={DataUsage.title}
          barColor={DataUsage.barColor}
          borderColor={DataUsage.borderColor}
          values={[2, network]}
        /> */}
      </StyledChartContainer>
      <StyledChartContainer>
        {/* <SystemLineChart
          labels={DataCpuUsage.labelsDataUsage}
          title={DataCpuUsage.title}
          barColor={DataCpuUsage.barColor}
          borderColor={DataCpuUsage.borderColor}
          values={[2,3,4,5,6,4]}
        /> */}
      </StyledChartContainer>
      <StyledChartContainer>
        {/* <BarChart
          labels={DataMemoryUsage.labelsDataUsage}
          title={DataMemoryUsage.title}
          barColor={DataMemoryUsage.barColor}
          borderColor={DataMemoryUsage.borderColor}
          values={[memory]}
        /> */}
      </StyledChartContainer>
    </StyledMainContainer>
  );
};

export default System;
